import React , { useState, useRef } from "react";
import {css} from "glamor";

/*
 * @Description: 带标题的背景卡片
 * @Author: 彭祥 (Email:245803627@qq.com)
 * @Date: 2020/7/6 13:31
 * @LastEditors: pengxiang
 * @LastEditTime: 2020/7/6 13:31
 */
const TitleCard = (props) => {

    const styles = {
        container:css({
            // position: 'relative',
            backgroundColor: 'rgba(0, 0, 0, 0.40)',
            // border:'5px solid red',
            borderRadius:'5px',
            // background:  "url(static/image/tree_bar_bg.png) repeat-x", //no-repeat // #00FF00 url(bgimage.gif) no-repeat fixed top;
            height: '100%',
            color:'#ffffff',
        }),
    };

    return(
        <div className={styles.container} style={props.style} >
            <div style={{ position: 'relative', top: '5px', color:'#ffffff'}} >
                <span style={{textAlign: 'center', display:'block',}}>{props.title || '标题'}</span>
            </div>
            <div style={{position: 'relative', top: '8px', left: '2%'}}>
                <div style={{display:'block',  width: '96%', height: '1px', borderTop: 'solid #ffffff 1px',}}> </div>
            </div>
            <div style={{top: '10px', color:'#ffffff', height: '100%'}}>
                {props.children}
            </div>
        </div>
    );
};
export default TitleCard;
